mapper.select:


    and train_project_id = #{trainProjectId}
    and train_project_name like concat('%',
        #{trainProjectName}, '%')

    and train_period = #{trainPeriod}
    and train_fee = #{trainFee}
    and train_addr_method = #{trainAddrMethod}

    and train_begin_time = #{trainBeginTime}
    and train_end_time = #{trainEndTime}
    and train_exam_method = #{trainExamMethod}

    and train_emp_id = #{trainEmpId}
    and train_emp_name like concat('%', #{trainEmpName},
        '%')

    and train_emp_idcard = #{trainEmpIdcard}
    and train_work_units_id = #{trainWorkUnitsId}
    and train_work_units_name like
        concat('%', #{trainWorkUnitsName}, '%')

    and train_hr_company_id = #{trainHrCompanyId}
    and train_hr_company_name like
        concat('%', #{trainHrCompanyName}, '%')



${params.dataScope}
