select dept_name,total_num,exit_num,age_num,ROUND((total_num-(exit_num+new_num)/2)/total_num*100,2) as stability_index,new_num as yearLessOne,yearOneToThree,yearThreeToFive,yearFiveToTen,yearTenToTwenty,yearGreatTwenty from( select dept_id, dept_name as dept_name,sum(total_emp) as total_num,sum(exit_emp) as exit_num, sum(new_emp) as new_num,sum(age_num) as age_num,sum(year1_3) as yearOneToThree, sum(year3_5) as yearThreeToFive, sum(year5_10) as yearFiveToTen,sum(year10_20) as yearTenToTwenty, sum(year20) as yearGreatTwenty from ( select emp_dept_id ,count(*) as total_emp , 0 as exit_emp,0 as new_emp,0 as age_num,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, count(*) as exit_emp,0 as new_emp,0 as age_num,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 and emp_status in (3,4) and emp_status_update_time >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 11 MONTH) and emp_status_update_time < DATE_ADD(LAST_DAY(NOW() - INTERVAL 0 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, count(*) as new_emp,0 as age_num,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 11 MONTH) and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 0 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp, count(*) as age_num,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 and emp_status_update_time = 6 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 11 MONTH) and emp_status_update_time < DATE_ADD(LAST_DAY(NOW() - INTERVAL 0 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp,0 as age_num,count(*) as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 35 MONTH) and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 12 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp,0 as age_num,0 as year1_3,count(*) as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee where disabled = 0 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 59 MONTH) and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 36 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp,0 as age_num,0 as year1_3,0 as year3_5,count(*) as year5_10,0 as year10_20, 0 as year20 from employee where disabled = 0 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 119 MONTH) and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 60 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp,0 as age_num ,0 as year1_3,0 as year3_5,0 as year5_10,count(*) as year10_20, 0 as year20 from employee where disabled = 0 and emp_hiredate >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 239 MONTH) and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 120 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id union all select emp_dept_id ,0 as total_emp, 0 as exit_emp, 0 as new_emp,0 as age_num ,0 as year1_3,0 as year3_5,0 as year5_10,count(*) as year10_20,count(*) as year20 from employee where disabled = 0 and emp_hiredate < DATE_ADD(LAST_DAY(NOW() - INTERVAL 240 MONTH), INTERVAL 1 DAY) GROUP BY emp_dept_id
)as countTable left join department on department.dept_id = countTable.emp_dept_id where countTable.emp_dept_id is not null GROUP BY dept_name ) as tempTable
and dept_name like CONCAT('%',#{deptName},'%')
