select unit_name,total_num,exit_num,age_num,ROUND((total_num-(exit_num+new_num)/2)/total_num*100,2) as stability_index,new_num as yearLessOne,yearOneToThree,yearThreeToFive,yearFiveToTen,yearTenToTwenty,yearGreatTwenty from( select unit_name ,sum(total_emp) as total_num,sum(exit_emp) as exit_num, sum(new_emp) as new_num,sum(age_emp) as age_num,sum(year1_3) as yearOneToThree, sum(year3_5) as yearThreeToFive, sum(year5_10) as yearFiveToTen,sum(year10_20) as yearTenToTwenty, sum(year20) as yearGreatTwenty from ( select unit_name ,count(*) as total_emp, 0 as exit_emp,0 as age_emp,0 as new_emp,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee left JOIN department on dept_id = emp_dept_id LEFT JOIN employing_units on unit_id = dept_unit_id where employee.disabled = 0 GROUP BY unit_name union all select unit_name ,0 as total_emp, count(*) as exit_emp,0 as age_emp,0 as new_emp,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee left JOIN department on dept_id = emp_dept_id LEFT JOIN employing_units on unit_id = dept_unit_id where employee.disabled = 0 and emp_status in (3,4) and emp_status_update_time >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 11 MONTH) and emp_status_update_time < DATE_ADD(LAST_DAY(NOW() - INTERVAL 0 MONTH), INTERVAL 1 DAY) GROUP BY unit_name union all select unit_name ,0 as total_emp, 0 as exit_emp,count(*) as age_emp,0 as new_emp,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_20,0 as year20 from employee left JOIN department on dept_id = emp_dept_id LEFT JOIN employing_units on unit_id = dept_unit_id where employee.disabled = 0 and emp_status = 6 and emp_status_update_time >=DATE_SUB(DATE_FORMAT(NOW(), '%Y-%m-01'), INTERVAL 11 MONTH) and emp_status_update_time < DATE_ADD(LAST_DAY(NOW() - INTERVAL 0 MONTH), INTERVAL 1 DAY) GROUP BY unit_name union all select unit_name ,0 as total_emp, 0 as exit_emp,0 as age_emp,count(*) as new_emp,0 as year1_3,0 as year3_5,0 as year5_10,0 as year10_